import { useState, forwardRef, useContext } from "react"
import {
    Button,
    TextField,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    Slide,
} from "@mui/material"
import { Context } from "../context/Context"
import axios from "axios"

const Transition = forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />
})

export const Addbook = ({ open, handleClose, handleSubmit }) => {
    // const [username, setUsername] = useState("")
    // const [password, setPassword] = useState("")
    const [title, setTitle] = useState("");
    const [desc, setDesc] = useState("");
    const [bookname, setBookname] = useState("");
    const [author, setAuthor] = useState("");
    const [edition, setEdition] = useState("");
    const [price, setPrice] = useState("");
    const [copyright, setCopyright] = useState("");
    

    // const onSubmit = (event) => {
        
    //     event.preventDefault()
    //     handleSubmit(title, desc, bookname, author, edition,price, copyright)
    //     console.log(title, desc, bookname, author, edition,price, copyright);
    // }

    // const handleEnterKeyDown = (event) => {
    //     if (event.key === "Enter") {
    //         onSubmit(event)
    //     }
    // }

  //add book
   handleSubmit = async (e) => {
        e.preventDefault();
        const newPost = {
          username: user.username,
          title,
          desc,
        };
       axios.post("/");
    };
  const [file, setFile] = useState(null);
  const { user } = useContext(Context);
//   const handleSubmit = async (e) => {
//     e.preventDefault();
//     const newPost = {
//       username: user.username,
//       title,
//       desc,
//     };
//     if (file) {
//       const data =new FormData();
//       const filename = Date.now() + file.name;
//       data.append("name", filename);
//       data.append("file", file);
//       newPost.photo = filename;
//       try {
//         await axios.post("/upload", data);
//       } catch (err) {}
//     }
//     try {
//       const res = await axios.post("/posts", newPost);
//       window.location.replace("/post/" + res.data._id);
//     } catch (err) {}
//   };




    return (
        
            
        <Dialog
            open={open}
            TransitionComponent={Transition}
            keepMounted
            onSubmit={handleSubmit}
            onClose={handleClose}
          //  onKeyDown={handleEnterKeyDown}
        >
            <DialogTitle>Login</DialogTitle>
            <DialogContent>
                <TextField
                    autoFocus
                    margin="dense"
                    id="title"
                    label="Title"
                    type="text"
                    fullWidth
                    variant="standard"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
                <TextField
                    margin="dense"
                    id="description"
                    label="Discription"
                    type="text"
                    fullWidth
                    variant="standard"
                    value={ desc}
                    onChange={(e) => setDesc(e.target.value)}
                />
                  <TextField
                    margin="dense"
                    id="bookname"
                    label="Bookname"
                    type="text"
                    fullWidth
                    variant="standard"
                    value={ bookname}
                    onChange={(e) => setBookname(e.target.value)}
                />
                  <TextField
                    margin="dense"
                    id="author"
                    label="Author"
                    type="text"
                    fullWidth
                    variant="standard"
                    value={author}
                    onChange={(e) => setAuthor(e.target.value)}
                />
                  <TextField
                    margin="dense"
                    id="edition"
                    label="Edition"
                    type="text"
                    fullWidth
                    variant="standard"
                    value={edition}
                    onChange={(e) => setEdition(e.target.value)}
                />
                  <TextField
                    margin="dense"
                    id="price"
                    label="Price"
                    type="text"
                    fullWidth
                    variant="standard"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                />
                <TextField
                    margin="dense"
                    id="copyright"
                    label="Copyright"
                    type="text"
                    fullWidth
                    variant="standard"
                    value={copyright}
                    onChange={(e) => setCopyright(e.target.value)}
                />
            </DialogContent>
            <DialogActions>
                <Button variant="text" onClick={handleClose}>
                    Cancel
                </Button>
                <Button variant="contained" type="submit" onSubmit= {handleSubmit}>
                    Submit
                </Button>
            </DialogActions>
        </Dialog>
        
    )
}
 
export default Addbook;
