import "./booklist.css"
import axios from "axios"
import { useState } from "react"
import { useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import { Context } from "../context/Context";
import { useContext } from "react";
import * as React from 'react';
import Addbook from '../addbook/Addbook'
import Updatebook from "../updateBook/Updatebook";

export default function Booklist() {
  //add book 
 //by using add book component(dialog)
  const [openAddbook, setOpenAddbook] = React.useState(false)  //add book dialog
  const [openUpdatebook, setOpenUpdatebook] = React.useState(false) //update book dialog

 
  const handleLoginSubmit = (username, password) => {
  //  console.log(username, password);
   setOpenAddbook(false)
  }
  const handleLoginClose = () => {
      setOpenAddbook(false)
  }
 
 //book list fetching from backend
    const [booklist, setBooklist] = useState([]);
    useEffect(()=>{
        const getBooks = async () => {
            const res = await axios.get("/books")
            // setUser(res.data)
            console.log("hello", res.data);
           // console.log(booklist[5]._id);
            setBooklist(res.data)

        };
        getBooks();
       
    },[] )

//delete book
const handleDelete = async (index,e) => {
  
  setBooklist(booklist.filter((v, i) => i !== index));
  try {
    const res   =  await axios.delete(`/books/${booklist[index]._id}`, {
        })
        
        console.log('delete',res.data);
      } catch(err) {}
}

//edit book working
const handleEdit = async (event, param) => {
  
 //const map1 = booklist.map(index)
 console.log("map1", event);
 console.log("param", param)

}


  return (
    <div>
         <div color="inherit" 
          onClick={() => {setOpenAddbook(true)}}>
             <i className="writeIcon fas fa-plus"></i>
           
          </div>
      <table> 
        <thead>
        <tr>
          <th colSpan="4">Book List</th>
        </tr>
        <tr>
          <th> index</th>
          <th>Book Name</th>
          <th>Author</th>
          <th>Edition</th>
          <th>price</th>
          <th>quantity</th>
          <th>Title</th>
          <th>Description</th>
          <th> Action</th>
         
        </tr>
        </thead>
        {booklist.map((c, index) => (
          <tr> 
            <td> {index+1}</td>
          <td>{c.bookname}</td>
          <td>{c.author}</td>
          <td>{c.edition}</td>
          <td>{c.price}</td>
          <td>{c.copyright}</td>
          <td>{c.title}</td>
          <td>{c.desc}</td>
          <td>
          <div className="singlePostEdit">
                <i className="singlePostIcon far fa-edit" onClick={event=> handleEdit(c)}> </i>
                <i className="singlePostIcon far fa-edit"  onClick={() => {setOpenUpdatebook(true)}}> </i>

               <i className="singlePostIcon far fa-trash-alt" onClick={e => handleDelete(index,e)}></i>   
                  </div>


          </td>
          </tr>

      ))}
      </table>
      <Addbook
          open={openAddbook}
          handleSubmit={handleLoginSubmit}
          handleClose={handleLoginClose}
      />

<Updatebook
        open={openUpdatebook}  
        handleSubmit={handleLoginSubmit}
        handleClose={handleLoginClose}
      />
    </div>
  
  
  )

  
}
