import "./register.css"
import { Link } from "react-router-dom";
import { useState } from "react";
import axios from "axios";


export default function Register() {
  const [username, setUsername] = useState("")
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [role, setRole] = useState("")
  const [error, setError] = useState(false);
  const state = {
    teams: [],
    selectedTeam: ""
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(false);
    try {
      const res = await axios.post("auth/register", {
        username,
        email,
        password,
        role
      });
   console.log('result', res)
    //  res.data && window.location.replace("/login");
    } catch (err) {
      setError(true);
    }
  };
 

 
  return (
   



    <div className="register">
        <div className="register-alignment">
        <span className="registerTitle">Register</span>
        <select value={this.state.selectedTeam}
              onChange={(e) => this.setState({selectedTeam: e.target.value})}>
        {this.state.teams.map((team) => <option key={team.value} value={team.value}>{team.display}</option>)}
      </select>

      <form action="" className="registerForm" onSubmit={handleSubmit}>
      <label >Username</label>
          <input type="text" className="registerInput" 
          placeholder="Enter your username......."
          onChange={e=>setUsername(e.target.value)} 
          
          />

          <label >Email</label>
          <input type="text" className="registerInput"
           placeholder="Enter your email......."
           onChange={e=>setEmail(e.target.value)} />

          <label >Password</label>
          <input type="password" className="registerInput" 
          placeholder="Enter your password......." 
          onChange={e=>setPassword(e.target.value)} />


            <label >Role</label>
          <input type="password" className="registerInput" 
          placeholder="Enter your password......." 
          onChange={e=>setRole(e.target.value)} />


          
          <button className="registerButton" type="submit" >Register</button>
          <button className="registerLoginButton">
      <Link className="link" to="/login">Login</Link>
      </button>
      </form>
      
      {error && <span style={{color:"red", marginTop:"10px"}}>Something went wrong!</span>}
      </div>
     
    </div>
  )
}
