import React, { useEffect } from 'react'
import { useState } from "react"
import axios from "axios"

export default function BBL(params) {
    const [userId, setUserId] = useState("")
    const [username, setUsername] = useState("")
    const [quantity, setQuantity] = useState("");
    const [booklist, setBooklist] = useState([]);

    useEffect(() => {
    
       console.log(params);
        })
    function returnBookHandler (e){
        if(e){
            console.log(e.quantity);
            setQuantity(e.quantity+1)
            console.log(e.quantity);

        }
    }

    const handleDelete = async (index, e) => {
        console.log('check');

        setBooklist(booklist.filter((v, i) => i !== index));
        console.log('check1');
        try {
          const res = await axios.delete(`/books/${booklist[index]._id}`, {
          })
    
          console.log('delete', res.data);
        } catch (err) { }
      }

  return (
    <div>
   <table>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Book Name </th>
                        <th>Title</th>
                        <th>Author</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>

                    {
                        params.bookList.map((b, i) => (
                            (b.borrowedBy.includes(params.user_id))
                            &&
                                <tr key={i}>
                                    <td>{i}</td>
                                    <td>{b.bookname} </td>
                                    <td>{b.title}</td>
                                    <td>{b.author}</td>
                                    <td>
                                        <button type=''  onClick={(e) =>  handleDelete(b)}> Return </button>
                                    </td>
                                </tr>
                        ))
                    }
                </tbody>
            </table>


    </div>
  )
}
