import "./booklist.css"
import axios from "axios"
import { useState } from "react"
import { useEffect } from "react";
import { Link } from "react-router-dom";
import { Context } from "../context/Context";
import { useContext } from "react";
import * as React from 'react';

import Addbook from '../addbook/Addbook'

export default function Booklist() {

  //add book 
 //by using add book component 
  const [openAddbook, setOpenAddbook] = React.useState(false)
  const { user } = useContext(Context);
 

  const handleLoginSubmit = (username, password) => {
  //  console.log(username, password);
   setOpenAddbook(false)
  }
  const handleLoginClose = () => {
      setOpenAddbook(false)
  }
 
 
  
  
//book list fetching from backend
    const [booklist, setBooklist] = useState([]);
    useEffect(()=>{
        const fetchUser = async () => {
            const res = await axios.get("/books")
            // setUser(res.data)
            console.log("hello", res.data);
            setBooklist(res.data)

        };
        fetchUser();
       
    },[] )

//delete book
const handleDelete = async () => {
  try {
    await axios.delete(`/books/${Booklist._id}`, {
      data: { username: user.username },
    });
    window.location.replace("/");
  } catch (err) {}
};


  return (
    <div>

{/* <button className="loginRegisterButton">
        <Link  className="link" to="/register">Register</Link>
        </button> */}

         <button color="inherit" 
          onClick={() => {setOpenAddbook(true)}}>
             <i className="writeIcon fas fa-plus"></i>
           
          </button>
      <table> 
        <thead>
        <tr>
          <th colSpan="4">Book List</th>
        </tr>
        <tr>
          <th>Book Name</th>
          <th>Author</th>
          <th>Edition</th>
          <th>price</th>
          <th>Copyright</th>
          <th>Title</th>
          <th>Description</th>
          <th> Action</th>
         
        </tr>
        </thead>
        {booklist.map((c) => (
          <tr> 
          <td>{c.bookname}</td>
          <td>{c.author}</td>
          <td>{c.edition}</td>
          <td>{c.price}</td>
          <td>{c.copyright}</td>
          <td>{c.title}</td>
          <td>{c.desc}</td>
          <td>
          <div className="singlePostEdit">
                <i className="singlePostIcon far fa-edit"> </i>

               <i className="singlePostIcon far fa-trash-alt" onClick={handleDelete}></i>   
                  </div>


          </td>
         
         
      
         
        
          </tr>
        
      
     

      ))}
      </table>

      <Addbook
          open={openAddbook}
          handleSubmit={handleLoginSubmit}
          handleClose={handleLoginClose}
      />
    </div>
  
  
  )

  
}
