import { useState, forwardRef, useContext } from "react"
import {
    Button,
    TextField,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    Slide,
} from "@mui/material"
import { Context } from "../context/Context"
import axios from "axios"
import { useEffect } from "react"

const Transition = forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />
})

export const Addbook = ({ open, handleClose, handleSubmit, bookData, action }) => {
    // const [username, setUsername] = useState("")
    // const [password, setPassword] = useState("")
    const [title, setTitle] = useState("");
    const [desc, setDesc] = useState("");
    const [bookname, setBookname] = useState("");
    const [author, setAuthor] = useState("");
    const [edition, setEdition] = useState("");
    const [price, setPrice] = useState("");
    const [copyright, setCopyright] = useState("");
    var [books, setBooks] = useState("");
    const { user } = useContext(Context);

    useEffect(() => {
        setTitle(bookData.title);
        setDesc(bookData.desc);
        setBookname(bookData.bookname);
        setAuthor(bookData.author);
        setEdition(bookData.edition);
        setPrice(bookData.price);
        setCopyright(bookData.copyright);
    }, []);

    console.log('hii', bookData, action);

    handleSubmit = async (e) => {

        e.preventDefault()
        // handleSubmit(title, desc, bookname, author, edition,price, copyright)
        // console.log(title, desc, bookname, author, edition,price, copyright);
        const newPost = {
            username: user.username, title, desc, bookname, author, edition, price, copyright,

        };

        const res = await axios.post("/books", newPost);
        console.log('BOOKS', res.data);
        setBooks(res.data);
        window.location.replace("/");


    };
   //Edit book 
   const handleUpdate = async (index, e) => {
  console.log('update', index, e);
   
    try {
      const res =  await axios.put(`/books/${books[index][0]}`, {
        username: user.username ,
        title, desc, bookname, author, edition, price, copyright,

      })

      console.log('update', res.data);
    } catch (err) { }
  }



    console.log(books);

    const handleEnterKeyDown = (event) => {
        if (event.key === "Enter") {
            handleSubmit(event)
        }
    }





    // const [file, setFile] = useState(null);





    return (


        <Dialog
            open={open}
            TransitionComponent={Transition}
            keepMounted
            onClose={handleClose}
            onKeyDown={handleEnterKeyDown}
        >

            {
                (action === "edit") ? (
                    <div>
                        <DialogTitle>Edit {bookData.bookname} book</DialogTitle>
                        <DialogContent>
                            <input
                                autoFocus
                                margin="dense"
                                id="title"
                                label="Title"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}
                            />
                            <TextField
                                margin="dense"
                                id="description"
                                label="Description"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={desc}
                                onChange={(e) => setDesc(e.target.value)}
                            />
                            <TextField
                                margin="dense"
                                id="bookname"
                                label="Bookname"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={bookname}
                                onChange={(e) => setBookname(e.target.value)}
                            />
                            <TextField
                                margin="dense"
                                id="author"
                                label="Author"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={author}
                                onChange={(e) => setAuthor(e.target.value)}
                            />
                            <TextField
                                margin="dense"
                                id="edition"
                                label="Edition"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={edition}
                                onChange={(e) => setEdition(e.target.value)}
                            />
                            <TextField
                                margin="dense"
                                id="price"
                                label="Price"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={price}
                                onChange={(e) => setPrice(e.target.value)}
                            />
                            <TextField
                                margin="dense"
                                id="copyright"
                                label="Copyright"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={copyright}
                                onChange={(e) => setCopyright(e.target.value)}
                            />
                        </DialogContent>
                        <DialogActions>
                <Button variant="text" onClick={handleClose}>
                    Cancel
                </Button>
                <Button variant="contained" onClick={handleUpdate}>
                    Update
                </Button>
            </DialogActions>
                    </div>

                )
                    :

                    <div>
                        <DialogTitle>Add new book</DialogTitle>
                        <DialogContent>


                            <TextField
                                autoFocus
                                margin="dense"
                                id="title"
                                label="Title"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}
                            />
                            <TextField
                                margin="dense"
                                id="description"
                                label="Discription"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={desc}
                                onChange={(e) => setDesc(e.target.value)}
                            />
                            <TextField
                                margin="dense"
                                id="bookname"
                                label="Bookname"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={bookname}
                                onChange={(e) => setBookname(e.target.value)}
                            />
                            <TextField
                                margin="dense"
                                id="author"
                                label="Author"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={author}
                                onChange={(e) => setAuthor(e.target.value)}
                            />
                            <TextField
                                margin="dense"
                                id="edition"
                                label="Edition"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={edition}
                                onChange={(e) => setEdition(e.target.value)}
                            />
                            <TextField
                                margin="dense"
                                id="price"
                                label="Price"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={price}
                                onChange={(e) => setPrice(e.target.value)}
                            />
                            <TextField
                                margin="dense"
                                id="copyright"
                                label="Copyright"
                                type="text"
                                fullWidth
                                variant="standard"
                                value={copyright}
                                onChange={(e) => setCopyright(e.target.value)}
                            />
                        </DialogContent>
                        <DialogActions>
                <Button variant="text" onClick={handleClose}>
                    Cancel
                </Button>
                <Button variant="contained" type="submit" onClick={handleSubmit}>
                    Submit
                </Button>
            </DialogActions>
                    </div>


            }

            
        </Dialog>

    )
}

export default Addbook;
