import React, { useEffect } from 'react'
import { useState } from "react"
import axios from "axios"

export default function BBL(params) {
    const [userId, setUserId] = useState("")
    const [username, setUsername] = useState("")
    const [quantity, setQuantity] = useState("");
    var [borrowlist, setBorrowlist] = useState([]);

    useEffect(() => {
    
       //console.log(params);
        })
    function returnBookHandler (e){
        if(e){
           
            borrowlist =   setBorrowlist(params.bookList.filter((b, i) => (
                (b.borrowedBy.includes(params.user_id)))))
        }

        console.log('check', borrowlist,setBorrowlist);
    }



  return (
    <div>
   <table>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Book Name </th>
                        <th>Title</th>
                        <th>Author</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>

                    {
                        params.bookList.map((b, i) => (
                            (b.borrowedBy.includes(params.user_id))
                            &&
                                <tr key={i}>
                                    <td>{i}</td>
                                    <td>{b.bookname} </td>
                                    <td>{b.title}</td>
                                    <td>{b.author}</td>
                                    <td>
                                        <button type=''  onClick={(e) => returnBookHandler(b)}> Return </button>
                                    </td>
                                </tr>
                        ))
                    }
                </tbody>
            </table>


    </div>
  )
}
