import React from 'react'
import { useState } from "react"

export default function BBL(params) {
    const [userId, setUserId] = useState("")
    const [username, setUsername] = useState("")
    const [quantity, setQuantity] = useState("");

    function returnBookHandler (e){
        if(e){
            console.log(e);
            setQuantity(e.quantity+1)
            console.log(e);

        }
    }

  return (
    <div>
   <table>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Book Name </th>
                        <th>Title</th>
                        <th>Author</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>

                    {
                        params.bookList.map((b, i) => (
                            (b.borrowedBy.includes(params.user_id))
                            &&
                                <tr key={i}>
                                    <td>{i}</td>
                                    <td>{b.bookname} </td>
                                    <td>{b.title}</td>
                                    <td>{b.author}</td>
                                    <td>
                                        <button type=''  onClick={(e) => returnBookHandler(b)}> Return </button>
                                    </td>
                                </tr>
                        ))
                    }
                </tbody>
            </table>


    </div>
  )
}
