  const [error, setError] = useState(false);
  const arr= ['admin', 'user']
console.log();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(false);
    try {
      const res = await axios.post("auth/register", {
        username,
        email,
        password,
        role
      });
   console.log('result', res)
    //  res.data && window.location.replace("/login");
