import React from 'react'
import { useState, forwardRef, useContext } from "react"

export default function BorrowedBookList(params) {
    const [userId, setUserId] = useState("")
    const [username, setUsername] = useState("")
    var [borrowedBy, setBorrowedBy] = useState([]);
    const [quantity, setQuantity] = useState("");


    function returnBookHandler (e){
        if(e){
            console.log(e._id);
        }
    }
    const handleReturnBook = async ( e) => {
      
        try {
            setUserId(e._id);
            setUsername(e.username);
           
            setBorrowedBy(borrowedBy.filter(item => item!== e._id));
            
            setQuantity(e.quantity+1)
            window.location.replace("/");
        }
        catch (err) { }
      }

    return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Book Name </th>
                        <th>Title</th>
                        <th>Author</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>

                    {
                        params.bookList.map((b, i) => (
                            (b.borrowedBy.includes(params.user_id))
                            &&
                                <tr key={i}>
                                    <td>{i}</td>
                                    <td>{b.bookname} </td>
                                    <td>{b.title}</td>
                                    <td>{b.author}</td>
                                    {/* <td>
                                        <button type=''  onClick={(e) => returnBookHandler(b._id)}> Return </button>
                                    </td> */}
                                    <td>
                                        <button type=''  onClick={(e) => handleReturnBook(b)}> Return </button>
                                    </td>
                                </tr>
                        ))
                    }
                </tbody>
            </table>
        </div>
    )
}
